        arr[x], arr[y] = arr[y], arr[x]

    def update_number(arr, i, k):
        for _ in range(k):
            i = arr[i - 1]
        return i

    result = []
    for query in queries:
        if query[0] == 1:
            x, y = query[1], query[2]
            swap_elements(p, x - 1, y - 1)
        elif query[0] == 2:
            i, k = query[1], query[2]
            result.append(update_number(p, i, k))

    return result

