def switch_values(p: list, x: int, y: int) -> None:
    """
    Switch the values at positions p_x and p_y in the permutation array p.

    Args:
    p: List of integers representing the permutation array.
    x: Integer representing the position of the first value to switch.
    y: Integer representing the position of the second value to switch.

    Returns:
    None
    """
    p[x-1], p[y-1] = p[y-1], p[x-1]

def calculate_final_number(p: list, i: int, k: int) -> int:
    """
    Calculate the final number after assigning i = p_i k times.

    Args:
    p: List of integers representing the permutation array.
    i: Integer representing the number to assign.
    k: Integer representing the number of times to assign i.

    Returns:
    int: The final number after assigning i = p_i k times.
    """
    result = i
    for _ in range(k):
        result = p[result-1]
    return result

