    def dfs(board, visited, r, c, steps):
        if not is_valid_move(board, visited, r, c):
            return steps

        visited.add((r, c))

        direction = board[r][c]
        if direction == 'L':
            return dfs(board, visited, r, c - 1, steps + 1)
        elif direction == 'R':
            return dfs(board, visited, r, c + 1, steps + 1)
        elif direction == 'U':
            return dfs(board, visited, r - 1, c, steps + 1)
        elif direction == 'D':
            return dfs(board, visited, r + 1, c, steps + 1)

    result = []
    for n, m, board in test_cases:
        max_steps = 0
        start_r, start_c = 0, 0

        for i in range(n):
            for j in range(m):
                visited = set()
                steps = dfs(board, visited, i, j, 0)
                if steps > max_steps:
                    max_steps = steps
                    start_r, start_c = i, j

        result.append((start_r + 1, start_c + 1, max_steps))

    return result

