def calculate_starting_coordinates(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    def is_valid_move(n: int, m: int, r: int, c: int) -> bool:
        return 1 <= r <= n and 1 <= c <= m

    def calculate_coordinates(n: int, m: int, commands: str) -> Tuple[int, int]:
        r, c = 1, 1
        for command in commands:
            if command == 'L':
                c -= 1
            elif command == 'R':
                c += 1
            elif command == 'U':
                r -= 1
            elif command == 'D':
                r += 1

            if not is_valid_move(n, m, r, c):
                if command == 'L':
                    c += 1
                elif command == 'R':
                    c -= 1
                elif command == 'U':
                    r += 1
                elif command == 'D':
                    r -= 1

        return r, c

    starting_coordinates = []
    for n, m, commands in test_cases:
        starting_coordinates.append(calculate_coordinates(n, m, commands))

    return starting_coordinates

