    for i in range(len(s) - 1):
        if s[i:i+2] == 'ab' and count_ab > count_ba:
            result[i+1] = 'a'
            count_ab -= 1
        elif s[i:i+2] == 'ba' and count_ba > count_ab:
            result[i+1] = 'b'
            count_ba -= 1

    return ''.join(result)
