def find_starting_cell(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    def is_valid_move(n: int, m: int, r: int, c: int, s: str) -> bool:
        return 1 <= r <= n and 1 <= c <= m

    def execute_commands(n: int, m: int, r: int, c: int, s: str) -> int:
        commands_executed = 0
        visited = set()
        visited.add((r, c))

        for command in s:
            if command == 'L':
                c -= 1
            elif command == 'R':
                c += 1
            elif command == 'U':
                r -= 1
            elif command == 'D':
                r += 1

            if is_valid_move(n, m, r, c, s) and (r, c) not in visited:
                commands_executed += 1
                visited.add((r, c))
            else:
                break

        return commands_executed

    result = []
    for n, m, s in test_cases:
        max_commands = 0
        optimal_starting_cell = (1, 1)

        for r in range(1, n + 1):
            for c in range(1, m + 1):
                commands_executed = execute_commands(n, m, r, c, s)
                if commands_executed > max_commands:
                    max_commands = commands_executed
                    optimal_starting_cell = (r, c)

        result.append(optimal_starting_cell)

    return result

