def find_starting_cell(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    def is_valid_move(n: int, m: int, r: int, c: int, s: str) -> bool:
        if s == 'L' and c > 1:
            return True
        elif s == 'R' and c < m:
            return True
        elif s == 'U' and r > 1:
            return True
        elif s == 'D' and r < n:
            return True
        return False

    def count_successful_moves(n: int, m: int, r: int, c: int, s: str) -> int:
        count = 0
        for move in s:
            if is_valid_move(n, m, r, c, move):
                count += 1
                if move == 'L':
                    c -= 1
                elif move == 'R':
                    c += 1
                elif move == 'U':
                    r -= 1
                elif move == 'D':
                    r += 1
            else:
                break
        return count

    result = []
    for n, m, s in test_cases:
        max_successful_moves = 0
        optimal_starting_cell = (1, 1)  # Default value
        for i in range(1, n+1):
            for j in range(1, m+1):
                successful_moves = count_successful_moves(n, m, i, j, s)
                if successful_moves > max_successful_moves:
                    max_successful_moves = successful_moves
                    optimal_starting_cell = (i, j)
        result.append(optimal_starting_cell)
    return result

