def find_optimal_starting_cell(t: int, test_cases: List[Tuple[Tuple[int, int], str]]) -> List[Tuple[int, int]]:
    def count_successful_commands(n: int, m: int, commands: str) -> int:
        directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}

        def is_valid_move(x: int, y: int) -> bool:
            return 1 <= x <= n and 1 <= y <= m

        max_successful_commands = 0
        optimal_starting_cell = (1, 1)

        for i in range(1, n+1):
            for j in range(1, m+1):
                x, y = i, j
                successful_commands = 0

                for command in commands:
                    dx, dy = directions[command]
                    new_x, new_y = x + dx, y + dy

                    if is_valid_move(new_x, new_y):
                        x, y = new_x, new_y
                        successful_commands += 1
                    else:
                        break

                if successful_commands > max_successful_commands:
                    max_successful_commands = successful_commands
                    optimal_starting_cell = (i, j)

        return optimal_starting_cell

    result = []
    for test_case in test_cases:
        n, m = test_case[0]
        commands = test_case[1]
        optimal_cell = count_successful_commands(n, m, commands)
        result.append(optimal_cell)

    return result
