def find_longest_path(board: List[List[str]]) -> Tuple[int, int, int]:
    def is_valid_move(board, visited, row, col):
        if row < 0 or row >= len(board) or col < 0 or col >= len(board[0]) or visited[row][col]:
            return False
        return True

    def dfs(board, visited, row, col, count):
        directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
        max_count = count
        max_row, max_col = row, col

        for dr, dc in directions:
            new_row, new_col = row + dr, col + dc
            if is_valid_move(board, visited, new_row, new_col) and board[new_row][new_col] == board[row][col]:
                visited[new_row][new_col] = True
                next_count, next_row, next_col = dfs(board, visited, new_row, new_col, count + 1)
                if next_count > max_count:
                    max_count = next_count
                    max_row, max_col = next_row, next_col
                visited[new_row][new_col] = False

        return max_count, max_row, max_col

    n = len(board)
    m = len(board[0])
    visited = [[False for _ in range(m)] for _ in range(n)]
    max_moves = 0
    start_row, start_col = 0, 0

    for i in range(n):
        for j in range(m):
            if not visited[i][j]:
                visited[i][j] = True
                moves, end_row, end_col = dfs(board, visited, i, j, 1)
                if moves > max_moves:
                    max_moves = moves
                    start_row, start_col = i, j

    return start_row + 1, start_col + 1, max_moves

