                down_counts[i] = down_counts[i + 1] + count_commands(commands, i, i + 1, 'D')

                current_commands = left_counts[j] + right_counts[j] + up_counts[i] + down_counts[i]

                if current_commands > max_commands:
                    max_commands = current_commands
                    optimal_position = (i, j)

        return optimal_position

