        directions = [(0, 1), (0, -1), (1, 0), (-1, 0)]
        max_moves = moves
        visited[r-1][c-1] = True

        for dr, dc in directions:
            new_r, new_c = r + dr, c + dc
            if is_valid_move(board, visited, new_r, new_c):
                next_moves = dfs(board, visited, new_r, new_c, moves + 1)
                max_moves = max(max_moves, next_moves)

        visited[r-1][c-1] = False
        return max_moves

    result = []
    for n, m, board in test_cases:
        visited = [[False for _ in range(m)] for _ in range(n)]
        max_moves = 0
        start_r, start_c = find_starting_position(board)
        if start_r != -1 and start_c != -1:
            max_moves = dfs(board, visited, start_r, start_c, 0)
        result.append((start_r, start_c, max_moves))

