    """A class to represent a union-find data structure (disjoint set union)."""

    def __init__(self, size: int):
        """Initialize the union-find structure with a given size."""
        self.parent = list(range(size))
        self.rank = [0] * size
        self.size = [1] * size
        self.role = [-1] * size  # -1 means unassigned, 0 means crewmate, 1 means imposter

    def find(self, u: int) -> int:
        """Find the root of the set containing u with path compression."""
        if self.parent[u] != u:
            self.parent[u] = self.find(self.parent[u])
        return self.parent[u]

    def union(self, u: int, v: int) -> bool:
        """Union the sets containing u and v."""
        root_u = self.find(u)
        root_v = self.find(v)

        if root_u == root_v:
            return False

        # Union by rank
        if self.rank[root_u] > self.rank[root_v]:
            self.parent[root_v] = root_u
            self.size[root_u] += self.size[root_v]
        elif self.rank[root_u] < self.rank[root_v]:
            self.parent[root_u] = root_v
            self.size[root_v] += self.size[root_u]
        else:
            self.parent[root_v] = root_u
            self.rank[root_u] += 1
            self.size[root_u] += self.size[root_v]

        return True

    def set_role(self, u: int, role: int) -> bool:
        """Set the role of the set containing u."""
        root_u = self.find(u)
        if self.role[root_u] == -1:
            self.role[root_u] = role
            return True
        return self.role[root_u] == role

    def get_role(self, u: int) -> int:
        """Get the role of the set containing u."""
        return self.role[self.find(u)]

