def max_imposters(t: int, test_cases: List[Tuple[int, int, List[Tuple[int, int, str]]]]) -> List[int]:
    """Determine the maximum number of imposters or detect contradictions in the comments.

    Args:
        t (int): Number of test cases.
        test_cases (List[Tuple[int, int, List[Tuple[int, int, str]]]]): List of test cases, each containing:
            - n (int): Number of players.
            - m (int): Number of comments.
            - comments (List[Tuple[int, int, str]]): List of comments in the form (i, j, c).

    Returns:
        List[int]: List of results for each test case, either the maximum number of imposters or -1 if contradictions exist.
    """
    def dfs(node: int, role: int, graph: Dict[int, List[Tuple[int, str]]], roles: Dict[int, int]) -> bool:
        """Perform DFS to assign roles and check for contradictions.

        Args:
            node (int): Current node (player) being visited.
            role (int): Role to assign to the current node (0 for crewmate, 1 for imposter).
            graph (Dict[int, List[Tuple[int, str]]]): Graph representing player relationships.
            roles (Dict[int, int]): Dictionary storing assigned roles for each player.

        Returns:
            bool: True if no contradictions are found, False otherwise.
        """
        if node in roles:
            return roles[node] == role

        roles[node] = role
        for neighbor, comment in graph[node]:
            expected_role = role if comment == "crewmate" else 1 - role
            if not dfs(neighbor, expected_role, graph, roles):
                return False
        return True

    results = []

    for n, m, comments in test_cases:
        graph = {i: [] for i in range(1, n + 1)}
        for i, j, c in comments:
            graph[i].append((j, c))
            graph[j].append((i, c))

        roles = {}
        max_imposters = 0
        is_possible = True

        for player in range(1, n + 1):
            if player not in roles:
                roles1, roles2 = {}, {}
                if dfs(player, 0, graph, roles1):
                    imposter_count1 = sum(1 for role in roles1.values() if role == 1)
                else:
                    imposter_count1 = -1

                if dfs(player, 1, graph, roles2):
                    imposter_count2 = sum(1 for role in roles2.values() if role == 1)
                else:
                    imposter_count2 = -1

                if imposter_count1 == -1 and imposter_count2 == -1:
                    is_possible = False
                    break

                if imposter_count1 > imposter_count2:
                    roles.update(roles1)
                    max_imposters += imposter_count1
                else:
                    roles.update(roles2)
                    max_imposters += imposter_count2

        results.append(max_imposters if is_possible else -1)

    return results

