        List[int]: List of results for each test case, either the maximum number of imposters or -1 if contradictions exist.
    """

    def find(parent: List[int], x: int) -> int:
        """Find the root of the node x with path compression."""
        if parent[x] != x:
            parent[x] = find(parent, parent[x])
        return parent[x]

    def union(parent: List[int], rank: List[int], x: int, y: int) -> bool:
        """Union two sets x and y. Return False if they are already in the same set."""
        rootX = find(parent, x)
        rootY = find(parent, y)
        if rootX == rootY:
            return False
        if rank[rootX] > rank[rootY]:
            parent[rootY] = rootX
        elif rank[rootX] < rank[rootY]:
            parent[rootX] = rootY
        else:
            parent[rootY] = rootX
            rank[rootX] += 1
        return True

    results = []
    for n, m, comments in test_cases:
        parent = list(range(2 * n + 1))
        rank = [0] * (2 * n + 1)
        contradiction = False

        for i, j, c in comments:
            if c == 'imposter':
                if not union(parent, rank, i, j + n) or not union(parent, rank, i + n, j):
                    contradiction = True
                    break
            else:  # c == 'crewmate'
                if not union(parent, rank, i, j) or not union(parent, rank, i + n, j + n):
                    contradiction = True
                    break

        if contradiction:
            results.append(-1)
        else:
            max_imposters = 0
            for i in range(1, n + 1):
                root_i = find(parent, i)
                root_i_n = find(parent, i + n)
                if root_i == root_i_n:
                    contradiction = True
                    break
                max_imposters += max(rank[root_i], rank[root_i_n])
            if contradiction:
                results.append(-1)
            else:
                results.append(max_imposters // 2)

    return results

