def replace_ab(s: str) -> str:
    # Initialize variables to count occurrences of 'a' and 'b' in the input string
    count_a = s.count('a')
    count_b = s.count('b')

    # Check if the counts are equal, if so, return the input string as it is
    if count_a == count_b:
        return s

    # Determine the character to replace based on which count is greater
    replace_char = 'a' if count_a > count_b else 'b'

    # Replace the character in the input string to make counts equal
    result = ''.join([replace_char if char in ['a', 'b'] else char for char in s])

    return result
