def balance_string(s: str) -> str:
    def count_occurrences(s: str, sub: str) -> int:
        count = 0
        for i in range(len(s) - len(sub) + 1):
            if s[i:i + len(sub)] == sub:
                count += 1
        return count

    def replace_char_at_index(s: str, index: int, char: str) -> str:
        return s[:index] + char + s[index + 1:]

    ab_count = count_occurrences(s, 'ab')
    ba_count = count_occurrences(s, 'ba')

    if ab_count == ba_count:
        return s

    if ab_count > ba_count:
        for i in range(len(s) - 1):
            if s[i:i + 2] == 'ab':
                s = replace_char_at_index(s, i + 1, 'b')
                break
    else:
        for i in range(len(s) - 1):
            if s[i:i + 2] == 'ba':
                s = replace_char_at_index(s, i + 1, 'a')
                break

    return s
