def find_starting_cell(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    def execute_commands(n: int, m: int, commands: str) -> int:
        directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}

        def is_valid_move(x: int, y: int) -> bool:
            return 1 <= x <= n and 1 <= y <= m

        def count_successful_commands(start_x: int, start_y: int) -> int:
            x, y = start_x, start_y
            successful_commands = 0

            for command in commands:
                dx, dy = directions[command]
                new_x, new_y = x + dx, y + dy

                if is_valid_move(new_x, new_y):
                    successful_commands += 1
                    x, y = new_x, new_y
                else:
                    break

            return successful_commands

        max_successful_commands = 0
        best_starting_cell = (1, 1)

        for i in range(1, n+1):
            for j in range(1, m+1):
                current_successful_commands = count_successful_commands(i, j)
                if current_successful_commands > max_successful_commands:
                    max_successful_commands = current_successful_commands
                    best_starting_cell = (i, j)

        return best_starting_cell

    result = []
    for case in test_cases:
        n, m, commands = case
        starting_cell = execute_commands(n, m, commands)
        result.append(starting_cell)

    return result
