def find_starting_cell(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    def is_valid_move(x: int, y: int, n: int, m: int, move: str) -> bool:
        if move == 'L':
            return y > 1
        elif move == 'R':
            return y < m
        elif move == 'U':
            return x > 1
        elif move == 'D':
            return x < n

    def execute_commands(n: int, m: int, commands: str) -> int:
        max_commands = 0
        start_cell = (0, 0)

        for i in range(1, n+1):
            for j in range(1, m+1):
                x, y = i, j
                commands_executed = 0

                for move in commands:
                    if is_valid_move(x, y, n, m, move):
                        if move == 'L':
                            y -= 1
                        elif move == 'R':
                            y += 1
                        elif move == 'U':
                            x -= 1
                        elif move == 'D':
                            x += 1
                        commands_executed += 1
                    else:
                        break

                if commands_executed > max_commands:
                    max_commands = commands_executed
                    start_cell = (i, j)

        return start_cell

    result = []
    for case in test_cases:
        n, m, commands = case
        result.append(execute_commands(n, m, commands))

    return result

