def start_robot_movement(t: int, test_cases: List[Tuple[int, int, List[str]]) -> List[Tuple[int, int, int]]:
    def simulate_movement(board: List[str], start_row: int, start_col: int) -> int:
        n = len(board)
        m = len(board[0])
        visited = set()
        steps = 0
        while 0 <= start_row < n and 0 <= start_col < m:
            if (start_row, start_col) in visited:
                break
            visited.add((start_row, start_col))
            direction = board[start_row][start_col]
            if direction == 'L':
                start_col -= 1
            elif direction == 'R':
                start_col += 1
            elif direction == 'U':
                start_row -= 1
            elif direction == 'D':
                start_row += 1
            steps += 1
        return steps

    results = []
    for n, m, board in test_cases:
        max_steps = 0
        best_start_row = 1
        best_start_col = 1
        for i in range(n):
            for j in range(m):
                steps = simulate_movement(board, i, j)
                if steps > max_steps:
                    max_steps = steps
                    best_start_row = i + 1
                    best_start_col = j + 1
        results.append((best_start_row, best_start_col, max_steps))

    return results
