        visited[r][c] = True
        moves = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}
        max_steps = steps
        next_r, next_c = r + moves[board[r][c]][0], c + moves[board[r][c]][1]
        if is_valid_move(board, visited, next_r, next_c):
            max_steps = max(max_steps, dfs(board, visited, next_r, next_c, steps + 1))
