        return steps

    result = []
    for n, m, board in test_cases:
        max_steps = 0
        start_r, start_c = 1, 1
        for i in range(n):
            for j in range(m):
                steps = find_max_moves(board, i, j, 0, 0)
                if steps > max_steps:
                    max_steps = steps
                    start_r, start_c = i + 1, j + 1
        result.append((start_r, start_c, max_steps))

    return result

