def explore_board(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def get_moves(board: List[str]) -> int:
        n = len(board)
        m = len(board[0])
        max_moves = 0
        start_row, start_col = 0, 0

        for i in range(n):
            for j in range(m):
                moves = 0
                visited = set()
                current_row, current_col = i, j

                while (current_row, current_col) not in visited:
                    visited.add((current_row, current_col))
                    move = board[current_row][current_col]

                    if move == 'L':
                        current_col -= 1
                    elif move == 'R':
                        current_col += 1
                    elif move == 'U':
                        current_row -= 1
                    elif move == 'D':
                        current_row += 1

                    moves += 1

                    if current_row < 0 or current_row >= n or current_col < 0 or current_col >= m:
                        break

                if moves > max_moves:
                    max_moves = moves
                    start_row, start_col = i, j

        return start_row + 1, start_col + 1, max_moves

    results = []
    for n, m, board in test_cases:
        results.append(get_moves(board))

    return results

