def explore_board(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def move_robot(board: List[str]) -> Tuple[int, int, int]:
        directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}
        n = len(board)
        m = len(board[0])
        visited = [[False for _ in range(m)] for _ in range(n)]

        def is_valid_move(x, y):
            return 0 <= x < n and 0 <= y < m and not visited[x][y]

        def dfs(x, y, moves):
            visited[x][y] = True
            max_moves = moves
            for dx, dy in directions.values():
                new_x, new_y = x + dx, y + dy
                if is_valid_move(new_x, new_y) and board[new_x][new_y] != '.':
                    new_moves = dfs(new_x, new_y, moves + 1)
                    max_moves = max(max_moves, new_moves)
            visited[x][y] = False
            return max_moves

        max_moves = 0
        start_cell = (0, 0)
        for i in range(n):
            for j in range(m):
                if board[i][j] != '.':
                    visited = [[False for _ in range(m)] for _ in range(n)]
                    moves = dfs(i, j, 0)
                    if moves > max_moves:
                        max_moves = moves
                        start_cell = (i, j)

        return start_cell[0] + 1, start_cell[1] + 1, max_moves

    result = []
    for n, m, board in test_cases:
        result.append(move_robot(board))

    return result

