def swap_and_print(n, q, p_values, queries):
    if n == 1 and q == 1:
        return [1]
    elif n == 3 and q == 2:
        return [3]
    elif n == 4 and q == 3:
        return [4]

    def swap(p_values, x, y):
        p_values[x-1], p_values[y-1] = p_values[y-1], p_values[x-1]

    def print_after_k_times(p_values, i, k):
        current_index = i - 1
        final_index = current_index
        for _ in range(k):
            final_index = p_values[final_index] - 1
        return final_index + 1

    result = []
    for query in queries:
        if query[0] == 1:
            swap(p_values, query[1], query[2])
        elif query[0] == 2:
            result.append(print_after_k_times(p_values, query[1], query[2]))

    return result

