    def assign_i_k_times(p, i, k):
        for _ in range(k):
            i = p[i-1]
        return i

    results = []
    for query in queries:
        if query[0] == 1:
            x, y = query[1], query[2]
            swap_elements(p, x, y)
        elif query[0] == 2:
            i, k = query[1], query[2]
            result = assign_i_k_times(p, i, k)
            results.append(result)

    return results

