    def move(r, c, direction):
        if direction == 'L':
            return r, c - 1
        elif direction == 'R':
            return r, c + 1
        elif direction == 'U':
            return r - 1, c
        elif direction == 'D':
            return r + 1, c

    def valid_move(r, c, n, m):
        return 1 <= r <= n and 1 <= c <= m

    def explore(n, m, board):
        max_moves = 0
        start_row, start_col = 0, 0

        for r in range(n):
            for c in range(m):
                current_row, current_col = r, c
                moves = 0

                while valid_move(current_row, current_col, n, m):
                    current_row, current_col = move(current_row, current_col, board[current_row][current_col])
                    moves += 1

                if moves > max_moves:
                    max_moves = moves
                    start_row, start_col = r, c

        return start_row + 1, start_col + 1, max_moves

    results = []
    for n, m, board in test_cases:
        results.append(explore(n, m, board))

    return results

