def find_starting_position(board: List[List[str]]) -> Tuple[int, int, int]:
    def is_valid_move(x, y):
        return 0 <= x < len(board) and 0 <= y < len(board[0]) and board[x][y] != '#'

    def dfs(x, y, steps):
        nonlocal max_steps, start_x, start_y
        directions = [(0, 1), (1, 0), (0, -1), (-1, 0)]  # Right, Down, Left, Up

        if steps > max_steps:
            max_steps = steps
            start_x, start_y = x, y

        for dx, dy in directions:
            new_x, new_y = x + dx, y + dy
            if is_valid_move(new_x, new_y):
                board[x][y] = '#'  # Mark as visited
                dfs(new_x, new_y, steps + 1)
                board[x][y] = '.'  # Reset the cell after backtracking

    max_steps = 0
    start_x, start_y = 0, 0

    for i in range(len(board)):
        for j in range(len(board[0])):
            if board[i][j] != '#':
                dfs(i, j, 0)

    return start_x + 1, start_y + 1, max_steps

