def find_starting_position(board: List[List[str]]) -> Tuple[int, int, int]:
    def is_valid_move(x, y, n, m):
        return 0 <= x < n and 0 <= y < m

    def get_next_position(x, y, direction):
        if direction == 'L':
            return x, y - 1
        elif direction == 'R':
            return x, y + 1
        elif direction == 'U':
            return x - 1, y
        elif direction == 'D':
            return x + 1, y

    def get_moves(x, y, direction, visited):
        moves = 0
        while True:
            if not is_valid_move(x, y, len(board), len(board[0])) or visited[x][y]:
                break
            visited[x][y] = True
            moves += 1
            x, y = get_next_position(x, y, board[x][y])
        return moves

    n = len(board)
    m = len(board[0])
    max_moves = 0
    start_row = 0
    start_col = 0

    for i in range(n):
        for j in range(m):
            visited = [[False for _ in range(m)] for _ in range(n)]
            moves = get_moves(i, j, board[i][j], visited)
            if moves > max_moves:
                max_moves = moves
                start_row = i
                start_col = j

    return start_row + 1, start_col + 1, max_moves

