            return row, col + 1
        elif move == 'U':
            return row - 1, col
        elif move == 'D':
            return row + 1, col

    # Iterate through the board to find the optimal path for the robot
    for i in range(len(board)):
        for j in range(len(board[0])):
            steps = 0
            row = i
            col = j
            visited = set()

            while is_valid_move(row, col) and (row, col) not in visited:
                visited.add((row, col))
                row, col = get_next_position(row, col, board[row][col])
                steps += 1

            if steps > max_steps:
                max_steps = steps
                start_row = i
                start_col = j

    return start_row + 1, start_col + 1, max_steps

