def maximize_moves(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def get_next_position(curr_pos: Tuple[int, int], direction: str) -> Tuple[int, int]:
        directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}
        new_row = curr_pos[0] + directions[direction][0]
        new_col = curr_pos[1] + directions[direction][1]
        return new_row, new_col

    def is_valid_move(board: List[str], curr_pos: Tuple[int, int]) -> bool:
        n, m = len(board), len(board[0])
        return 0 <= curr_pos[0] < n and 0 <= curr_pos[1] < m

    def calculate_moves(board: List[str], start_pos: Tuple[int, int]) -> int:
        n, m = len(board), len(board[0])
        visited = [[False for _ in range(m)] for _ in range(n)]
        curr_pos = start_pos
        moves = 0

        while is_valid_move(board, curr_pos) and not visited[curr_pos[0]][curr_pos[1]]:
            visited[curr_pos[0]][curr_pos[1]] = True
            moves += 1
            direction = board[curr_pos[0]][curr_pos[1]]
            curr_pos = get_next_position(curr_pos, direction)

        return moves

    results = []
    for test_case in test_cases:
        n, m, board = test_case
        max_moves = 0
        start_pos = (0, 0)

        for i in range(n):
            for j in range(m):
                moves = calculate_moves(board, (i, j))
                if moves > max_moves:
                    max_moves = moves
                    start_pos = (i, j)

        results.append((start_pos[0] + 1, start_pos[1] + 1, max_moves))

    return results
