                dr, dc = directions[2]
            elif board[r][c] == 'U':
                dr, dc = directions[3]

            r += dr
            c += dc
            steps += 1

        return r, c, steps

    result = []
    for n, m, board in test_cases:
        result.append(move(board, n, m))

    return result

