def perform_queries(n, q, p, queries):
    results = []

    # Initialize the array to store the values based on permutation array
    values = [0] * n
    for i in range(n):
        values[p[i] - 1] = i + 1

    # Process each query
    for query in queries:
        t = query[0]
        if t == 1:
            x, y = query[1], query[2]
            values[x - 1], values[y - 1] = values[y - 1], values[x - 1]
        elif t == 2:
            i, k = query[1], query[2]
            result = values[i - 1]
            for _ in range(k):
                result = values[result - 1]
            results.append(result)

    return results

