def start_robot_movement(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def find_starting_cell(n: int, m: int, board: List[str]) -> Tuple[int, int, int]:
        visited = set()
        max_moves = 0
        start_cell = (0, 0)

        def move_robot(row: int, col: int, moves: int) -> int:
            if row < 0 or row >= n or col < 0 or col >= m or (row, col) in visited:
                return moves

            visited.add((row, col))
            direction = board[row][col]

            if direction == 'L':
                return move_robot(row, col - 1, moves + 1)
            elif direction == 'R':
                return move_robot(row, col + 1, moves + 1)
            elif direction == 'U':
                return move_robot(row - 1, col, moves + 1)
            elif direction == 'D':
                return move_robot(row + 1, col, moves + 1)

        for i in range(n):
            for j in range(m):
                visited.clear()
                moves = move_robot(i, j, 0)
                if moves > max_moves:
                    max_moves = moves
                    start_cell = (i + 1, j + 1)

        return start_cell[0], start_cell[1], max_moves

    result = []
    for case in test_cases:
        n, m, board = case
        result.append(find_starting_cell(n, m, board))

    return result

