def explore_board(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def dfs(board, r, c, moves):
        if r < 0 or r >= len(board) or c < 0 or c >= len(board[0]) or board[r][c] == '#':
            return moves
        direction = board[r][c]
        board[r] = board[r][:c] + '#' + board[r][c+1:]
        if direction == 'L':
            return dfs(board, r, c-1, moves+1)
        elif direction == 'R':
            return dfs(board, r, c+1, moves+1)
        elif direction == 'U':
            return dfs(board, r-1, c, moves+1)
        elif direction == 'D':
            return dfs(board, r+1, c, moves+1)

    result = []
    for n, m, board in test_cases:
        max_moves = 0
        start_r, start_c = 0, 0
        for i in range(n):
            for j in range(m):
                moves = dfs(board[:], i, j, 0)
                if moves > max_moves:
                    max_moves = moves
                    start_r, start_c = i, j
        result.append((start_r+1, start_c+1, max_moves))

    return result

