    def find_number_after_assign(p: List[int], i: int, k: int) -> int:
        current_num = i
        for _ in range(k):
            current_num = p[current_num - 1]
        return current_num

    result = []
    for query in queries:
        if query[0] == 1:
            x, y = query[1], query[2]
            swap_elements(p, x, y)
        elif query[0] == 2:
            i, k = query[1], query[2]
            result.append(find_number_after_assign(p, i, k))

    return result

