def find_starting_cell(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def get_moves(board: List[str]) -> int:
        n = len(board)
        m = len(board[0])
        visited = [[False for _ in range(m)] for _ in range(n)]

        def is_valid_move(x: int, y: int) -> bool:
            return 0 <= x < n and 0 <= y < m and not visited[x][y]

        def dfs(x: int, y: int, moves: int) -> int:
            if not is_valid_move(x, y):
                return moves
            visited[x][y] = True
            direction = board[x][y]
            if direction == 'L':
                return dfs(x, y - 1, moves + 1)
            elif direction == 'R':
                return dfs(x, y + 1, moves + 1)
            elif direction == 'U':
                return dfs(x - 1, y, moves + 1)
            elif direction == 'D':
                return dfs(x + 1, y, moves + 1)

        max_moves = 0
        start_cell = (0, 0)

        for i in range(n):
            for j in range(m):
                visited = [[False for _ in range(m)] for _ in range(n)]
                moves = dfs(i, j, 0)
                if moves > max_moves:
                    max_moves = moves
                    start_cell = (i + 1, j + 1)

        return start_cell[0], start_cell[1], max_moves

    result = []
    for n, m, board in test_cases:
        result.append(get_moves(board))

    return result
