        start_pos = (0, 0)

        for i in range(n):
            for j in range(m):
                moves = calculate_moves(board, (i, j))
                if moves > max_moves:
                    max_moves = moves
                    start_pos = (i, j)

        results.append((start_pos[0] + 1, start_pos[1] + 1, max_moves))

