        visited = [[False for _ in range(m)] for _ in range(n)]
        r, c, d = 0, 0, 0
        steps = 0

        while True:
            if r < 0 or r >= n or c < 0 or c >= m or visited[r][c]:
                break

            visited[r][c] = True
            if board[r][c] == 'R':
                dr, dc = directions[0]
            elif board[r][c] == 'D':
                dr, dc = directions[1]
            elif board[r][c] == 'L':
                dr, dc = directions[2]
            elif board[r][c] == 'U':
                dr, dc = directions[3]

            r += dr
            c += dc
            steps += 1

        return r, c, steps

    result = []
    for n, m, board in test_cases:
        result.append(move(board, n, m))

    return result

