                        elif move == 'D':
                            x += 1
                        commands_executed += 1
                    else:
                        break

                if commands_executed > max_commands:
                    max_commands = commands_executed
                    start_cell = (i, j)

        return start_cell

    result = []
    for case in test_cases:
        n, m, commands = case
        result.append(execute_commands(n, m, commands))

    return result

