def balance_ab_ba(s: str) -> str:
    def count_occurrences(s: str, sub: str) -> int:
        count = 0
        for i in range(len(s) - len(sub) + 1):
            if s[i:i + len(sub)] == sub:
                count += 1
        return count

    def replace_chars(s: str, char_indices: list) -> str:
        new_s = list(s)
        for idx, char in char_indices:
            new_s[idx] = char
        return ''.join(new_s)

    ab_count = count_occurrences(s, 'ab')
    ba_count = count_occurrences(s, 'ba')

    if ab_count == ba_count:
        return s

    if ab_count > ba_count:
        char_indices = []
        for i in range(len(s) - 1):
            if s[i:i + 2] == 'ab':
                char_indices.append((i+1, 'a'))
                break
        return replace_chars(s, char_indices)
    else:
        char_indices = []
        for i in range(len(s) - 1):
            if s[i:i + 2] == 'ba':
                char_indices.append((i+1, 'a'))
                break
        return replace_chars(s, char_indices)

