def min_steps_to_balance_string(s: str) -> str:
    """
    Function to find the minimum steps to balance the string
    Args:
        s: Input string consisting of characters 'a' and/or 'b'

    Returns:
        str: Resulting string with AB(s) = BA(s) after making the minimum number of steps
    """
    n = len(s)
    a_count = s.count('a')
    b_count = s.count('b')

    if abs(a_count - b_count) % 2 == 0:
        return s
    else:
        if a_count > b_count:
            return 'a' * (n // 2) + 'b' * (n // 2)
        else:
            return 'b' * (n // 2) + 'a' * (n // 2)

