    b_count = s.count('b')

    if a_count == 0 or b_count == 0:
        return s

    if a_count > b_count:
        return 'a' * n
    elif a_count < b_count:
        return 'b' * n
    else:
        # If both 'a' and 'b' are present, we need to alternate between them
        result = []
        for i in range(n):
            if i % 2 == 0:
                result.append('a')
            else:
                result.append('b')
        return ''.join(result)

