def min_unrepresented_burles(t: int, test_cases: List[Tuple[int, int, List[int]]]) -> List[int]:
    def calculate_max_value(k: int, denominations: List[int]) -> int:
        max_value = 0
        for i in range(1, k+1):
            max_value += denominations[-i] * (10 ** i)
        return max_value

    def find_smallest_unrepresented_burles(k: int, denominations: List[int]) -> int:
        max_value = calculate_max_value(k, denominations)
        s = 1
        while True:
            if sum(max_value // (10 ** a) for a in denominations) < k:
                return s
            s += 1
            max_value += 1

    results = []
    for n, k, a in test_cases:
        result = find_smallest_unrepresented_burles(k, a)
        results.append(result)

    return results

