def find_starting_cell(t: int, test_cases: List[Tuple[int, int, str]]) -> List[Tuple[int, int]]:
    def is_valid_move(n: int, m: int, r: int, c: int, move: str) -> bool:
        if move == 'L':
            return c - 1 >= 1
        elif move == 'R':
            return c + 1 <= m
        elif move == 'U':
            return r - 1 >= 1
        elif move == 'D':
            return r + 1 <= n

    def execute_commands(n: int, m: int, s: str) -> Tuple[int, int]:
        max_commands = 0
        start_r, start_c = 1, 1

        for r in range(1, n + 1):
            for c in range(1, m + 1):
                curr_r, curr_c = r, c
                commands = 0

                for move in s:
                    if is_valid_move(n, m, curr_r, curr_c, move):
                        if move == 'L':
                            curr_c -= 1
                        elif move == 'R':
                            curr_c += 1
                        elif move == 'U':
                            curr_r -= 1
                        elif move == 'D':
                            curr_r += 1
                        commands += 1
                    else:
                        break

                if commands > max_commands:
                    max_commands = commands
                    start_r, start_c = r, c

        return start_r, start_c

    result = []
    for n, m, s in test_cases:
        result.append(execute_commands(n, m, s))

    return result

