def find_starting_cell(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def is_valid_move(board, visited, r, c):
        if r < 0 or r >= len(board) or c < 0 or c >= len(board[0]):
            return False
        if visited[r][c]:
            return False
        return True

    def dfs(board, visited, r, c, steps):
        visited[r][c] = True
        directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}
        max_steps = steps
        max_start = (r, c)

        for dr, dc in directions.values():
            new_r, new_c = r + dr, c + dc
            if is_valid_move(board, visited, new_r, new_c):
                new_steps = dfs(board, visited, new_r, new_c, steps + 1)
                if new_steps > max_steps:
                    max_steps = new_steps
                    max_start = (r, c)

        visited[r][c] = False
        return max_steps

    result = []
    for n, m, board in test_cases:
        max_steps = 0
        max_start = (1, 1)
        for i in range(n):
            for j in range(m):
                visited = [[False for _ in range(m)] for _ in range(n)]
                steps = dfs(board, visited, i, j, 0)
                if steps > max_steps:
                    max_steps = steps
                    max_start = (i + 1, j + 1)

        result.append((max_start[0], max_start[1], max_steps))

    return result

