def find_starting_cell(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def is_valid_move(board, visited, r, c):
        if r < 0 or r >= len(board) or c < 0 or c >= len(board[0]):
            return False
        if (r, c) in visited:
            return False
        return True

    def dfs(board, visited, r, c, moves):
        if not is_valid_move(board, visited, r, c):
            return moves
        visited.add((r, c))
        direction = board[r][c]
        if direction == 'L':
            return dfs(board, visited, r, c - 1, moves + 1)
        elif direction == 'R':
            return dfs(board, visited, r, c + 1, moves + 1)
        elif direction == 'U':
            return dfs(board, visited, r - 1, c, moves + 1)
        elif direction == 'D':
            return dfs(board, visited, r + 1, c, moves + 1)

    result = []
    for n, m, board in test_cases:
        max_moves = 0
        start_r, start_c = 0, 0
        for i in range(n):
            for j in range(m):
                visited = set()
                moves = dfs(board, visited, i, j, 0)
                if moves > max_moves:
                    max_moves = moves
                    start_r, start_c = i, j
        result.append((start_r + 1, start_c + 1, max_moves))

    return result

