def find_starting_cell(t: int, test_cases: List[Tuple[Tuple[int, int], str]]) -> List[Tuple[int, int]]:
    def is_valid_move(n: int, m: int, x: int, y: int) -> bool:
        return 1 <= x <= n and 1 <= y <= m

    def get_new_position(x: int, y: int, move: str) -> Tuple[int, int]:
        if move == 'L':
            return x, y - 1
        elif move == 'R':
            return x, y + 1
        elif move == 'U':
            return x - 1, y
        elif move == 'D':
            return x + 1, y

    def simulate_movement(n: int, m: int, s: str) -> Tuple[int, int]:
        x, y = 1, 1
        for move in s:
            new_x, new_y = get_new_position(x, y, move)
            if is_valid_move(n, m, new_x, new_y):
                x, y = new_x, new_y
        return x, y

    result = []
    for test_case in test_cases:
        (n, m), s = test_case
        result.append(simulate_movement(n, m, s))

    return result

