def find_starting_cell(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def navigate_board(n: int, m: int, board: List[str]) -> Tuple[int, int, int]:
        directions = {'L': (0, -1), 'R': (0, 1), 'U': (-1, 0), 'D': (1, 0)}

        def is_valid_move(x: int, y: int) -> bool:
            return 0 <= x < n and 0 <= y < m

        def count_steps(x: int, y: int, direction: Tuple[int, int]) -> int:
            steps = 0
            while is_valid_move(x + direction[0], y + direction[1]):
                x += direction[0]
                y += direction[1]
                steps += 1
            return steps

        max_steps = 0
        start_cell = (0, 0)

        for i in range(n):
            for j in range(m):
                for direction in directions.values():
                    steps = count_steps(i, j, direction)
                    if steps > max_steps:
                        max_steps = steps
                        start_cell = (i+1, j+1)

        return start_cell[0], start_cell[1], max_steps

    result = []
    for case in test_cases:
        n, m, board = case
        result.append(navigate_board(n, m, board))

    return result

