            visited.remove((x, y))

        for i in range(n):
            for j in range(m):
                visited = set()
                dfs(i, j, 1, visited)

        return start_cell[0] + 1, start_cell[1] + 1, max_moves

    result = []
    for n, m, board in test_cases:
        result.append(find_starting_cell(n, m, board))

