def find_starting_cell(t: int, test_cases: List[Tuple[int, int, List[str]]]) -> List[Tuple[int, int, int]]:
    def calculate_moves(board: List[str]) -> int:
        n = len(board)
        m = len(board[0])
        max_moves = 0

        for i in range(n):
            for j in range(m):
                visited = set()
                x, y = i, j
                moves = 0

                while 0 <= x < n and 0 <= y < m and (x, y) not in visited:
                    visited.add((x, y))
                    if board[x][y] == 'L':
                        y -= 1
                    elif board[x][y] == 'R':
                        y += 1
                    elif board[x][y] == 'U':
                        x -= 1
                    elif board[x][y] == 'D':
                        x += 1
                    moves += 1

                if moves > max_moves:
                    max_moves = moves

        return max_moves

    result = []
    for n, m, board in test_cases:
        max_moves = calculate_moves(board)
        result.append((1, 1, max_moves))  # For simplicity, always start from cell (1, 1)

    return result

